import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import { Form, Button, NavLink } from "react-bootstrap";

function App() {
  const [shortURL, setShortURL] = useState("");
  const [urlState, setUrl] = useState("");
  const [isValid, setValidity] = useState(false);
  var url = "";
  const handleURLChange = (event) => {
    url = event.target.value;
    axios.get("/barcodes/zxing/" + url).then((res) => {
      setShortURL(res.data);
      setUrl(url);
    });
  };

  const handleValidation = () => {
    axios.get("/barcodes/validateUrl" + url).then((res) => {
      setValidity(res.data);
      if (isValid) {
        alert("URL is valid");
      } else alert("URL is invalid");
    });
  };
  return (
    <div class="container mt-4">
      <div class="row">
        <div class="col-md-8 pr-0 mpr-15">
          <Form>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>URL</Form.Label>
              <Form.Control
                placeholder="Enter the URL"
                onChange={handleURLChange}
              />
            </Form.Group>
            <Button
              className="submitButton"
              variant="primary"
              type="submit"
              onClick={handleValidation}
            >
              Validate URL
            </Button>
          </Form>
        </div>
        <div class="col-md-4">
          <img src={require("file:///C:/QRCode/QRCode.png")} alt="hey"></img>
        </div>
      </div>
      <div class="row">
        <div class="col-md-8 pr-0 mpr-15"></div>
        <div class="col-md-4">
          <a href={"https://" + urlState} style={{ paddingLeft: 40 }}>
            {shortURL}
          </a>
        </div>
      </div>
    </div>
  );
}

export default App;
