import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import { Form, Button, NavLink } from "react-bootstrap";

function App() {
  const [qrCode, setQRCode] = useState("");
  var shortenedURL = "";
  var url = "";
  /*useEffect(() => {
    axios.get("/barcodes/zxing").then((res) => {
      setQRCode(res.data);
    });
  });*/
  const handleURLChange = (event) => {
    url = event.target.value;
    axios.get("/barcodes/zxing/" + url).then((res) => {
      setQRCode(res.data);
      //shortenedURL = res.data;
    });
  };
  return (
    <div class="container mt-4">
      <div class="row">
        <div class="col-md-8 pr-0 mpr-15">
          <Form>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>URL</Form.Label>
              <Form.Control
                placeholder="Enter the URL"
                onChange={handleURLChange}
              />
            </Form.Group>
            <Button className="submitButton" variant="primary" type="submit">
              Validate URL
            </Button>
          </Form>
        </div>
        <div class="col-md-4">
          <img src={require("file:///C:/QRCode/QRCode.png")} alt="hey"></img>
        </div>
      </div>
      <div class="row">
        <div class="col-md-8 pr-0 mpr-15"></div>
        <div class="col-md-4">
          {/*
          <NavLink
            className="navbar-item"
            exact
            activeClassName="is-active"
            style={{ paddingLeft: 40 }}
            to="https://www.google.com/"
          >
            Home
          </NavLink>*/}
          <a href={url} style={{ paddingLeft: 40 }}>
            {qrCode}
          </a>
        </div>
      </div>
    </div>
  );
}

export default App;
